mapper.insert:
insert into tb_logininfo(
login_id,
staff_id,
login_time
)values(
#{loginId},
#{staffId},
#{loginTime}
)

mapper.select:
select * from tb_operationinfo
where user_id=#{userId}
order by operation_time desc;

select * from tb_operationinfo
where operation_time>=#{start} and operation_time<=#{end}
order by operation_time desc;

select * from tb_logininfo
where staff_id=#{staffId}
order by login_time desc
limit #{start},#{rows}

mapper.update:
update tb_userinfo set
password=#{password}
where staff_id=#{staffId}
